const burger=document.querySelector(".burger"),burgerSpanTop=document.querySelector(".burger__span--top"),burgerSpanBottom=document.querySelector(".burger__span--bottom"),burgerPopup=document.querySelector(".burger__popup");let menuOpened=!1;function openBurger(){burgerSpanTop.style.transition=".3s all",burgerSpanTop.style.transform="rotate(45deg)",burgerSpanTop.style.top="25px",burgerSpanBottom.style.transition=".3s all",burgerSpanBottom.style.transform="rotate(-45deg)",burgerSpanBottom.style.top="25px",burgerPopup.style.display="block",menuOpened=!0}function closeBurger(){burgerSpanTop.style.transform="rotate(0deg)",burgerSpanTop.style.top="20px",burgerSpanBottom.style.transform="rotate(0deg)",burgerSpanBottom.style.top="30px",burgerPopup.style.display="none",menuOpened=!1}burger.addEventListener("click",(()=>{menuOpened?closeBurger():openBurger()})),document.querySelector("body").addEventListener("click",(e=>{!menuOpened||e.target.classList.contains("burger__popup")||e.target.classList.contains("burger")||closeBurger()}));